import React from 'react';
import { useTheme } from './ThemeContext';
import classNames from 'classnames';

const ThemeSwitcher = () => {
  const { isDarkTheme, toggleTheme, themeStyles } = useTheme();

  return (
    <button
      onClick={toggleTheme}
      className={classNames('theme-switcher-button', { 'dark-theme': isDarkTheme, 'light-theme': !isDarkTheme })}
      style={{
        background: themeStyles.buttonBackground,
        color: themeStyles.buttonColor,
        padding: '10px 20px',
        fontSize: '16px',
        cursor: 'pointer',
        transition: 'background-color 0.3s, color 0.3s, border-color 0.3s'
      }}
    >
      {isDarkTheme ? 'Switch to Light Theme' : 'Switch to Dark Theme'}
    </button>
  );
};

export default ThemeSwitcher;
