import React, { useEffect } from "react";
import solarEnergy from '../assets/Content pages/solarEnergy.png'
import styles from './content.css'
import { useTheme } from '../ThemeSwitch/ThemeContext';
import classNames from 'classnames';
const Content = () => {
  useEffect(() => {
    const handleClick = (e) => {
      const contentBtn = e.target;
      const content1 = contentBtn.nextElementSibling;

      // Проверяем, есть ли у текущей кнопки класс 'active'
      const isActive = contentBtn.classList.contains('active');

      // Если текущая кнопка активна, закрываем блок и удаляем класс 'active'
      if (isActive) {
        content1.style.maxHeight = null;
        contentBtn.classList.remove('active');
      } else {
        // Закрываем все блоки и удаляем класс 'active' у всех кнопок
        const allContentBlocks = document.querySelectorAll('.content1');
        const allButtons = document.querySelectorAll('.content_button');

        allContentBlocks.forEach(block => {
          block.style.maxHeight = null;
        });

        allButtons.forEach(btn => {
          btn.classList.remove('active');
        });

        // Открываем текущий блок и добавляем класс 'active' к текущей кнопке
        content1.style.maxHeight = content1.scrollHeight + 'px';
        contentBtn.classList.add('active');
      }
    };

    const contentBtns = document.querySelectorAll('.content_button');
    contentBtns.forEach(btn => btn.addEventListener('click', handleClick));

    // Очищаем обработчики событий при размонтировании компонента
    return () => {
      contentBtns.forEach(btn => btn.removeEventListener('click', handleClick));
    };
  }, []);

  return (
    <div className="content">
      <div className="content__container">
        <div className="text-content">
          <h1>Our commitment to green energy is paving the way for a cleaner, healthier planet. </h1>
          <button className="content_button content_button1">EV charging </button>
          <div className="content1">  
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </div>

          <button className="content_button">Solar Energy </button>
          <div className="content1">  
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </div>

          <button className="content_button">Wind Energy </button>
          <div className="content1">  
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </div>

          <button className="content_button">Hydropower </button>
          <div className="content1">  
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </div>
        </div>

        <div className="pic-content">
          <img src={solarEnergy} alt="Solar Energy" />
        </div>
      </div>
    </div>
  );
}

export default Content;
