import React, { createContext, useState, useContext } from 'react';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
  const [isDarkTheme, setIsDarkTheme] = useState(false);

  const toggleTheme = () => {
    setIsDarkTheme(prevTheme => !prevTheme);
  };

  const themeStyles = {
    background: isDarkTheme ? '#333' : '#FFF',
    color: isDarkTheme ? '#FFF' : '#000',
    buttonBackground: isDarkTheme ? '#555' : '#DDD',
    buttonColor: isDarkTheme ? '#FFF' : '#000',
    borderColor: isDarkTheme ? '#FFF' : '#000',
    image: isDarkTheme ? 'dark-image.png' : 'light-image.png', // пути к вашим изображениям
  };

  return (
    <ThemeContext.Provider value={{ isDarkTheme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
