import React from 'react';
import { useTheme } from './ThemeContext';
import classNames from 'classnames';
import styles from './theme_switcher.css';

const ThemeSwitcher = () => {
  const { isDarkTheme, toggleTheme, themeStyles } = useTheme();

  return (
    <div
      className={classNames('theme-switcher-container', {
        'dark-theme': isDarkTheme,
        'light-theme': !isDarkTheme,
      })}
      style={{
        background: themeStyles.background,
        color: themeStyles.color,
      }}
    >
      <button
        onClick={toggleTheme}
        className={classNames('theme-switcher-button', {
          'dark-theme': isDarkTheme,
          'light-theme': !isDarkTheme,
        })}
        style={{
          background: themeStyles.buttonBackground,
          color: themeStyles.buttonColor,
          border: `1px solid ${themeStyles.borderColor}`,
          padding: '10px 20px',
          fontSize: '16px',
          cursor: 'pointer',
          transition: 'background-color 0.3s, color 0.3s, border-color 0.3s',
          position: 'sticky',
          top: '0', // Останавливаем кнопку в верхней части своего контейнера
          zIndex: 1000, // Устанавливаем высокий z-index для того, чтобы кнопка была поверх других элементов
        }}
      >
        {isDarkTheme ? 'Switch to Light Theme' : 'Switch to Dark Theme'}
      </button>
    </div>
  );
};

export default ThemeSwitcher;
