import React from 'react';
import ThemeStyles from '../ThemeSwitch/theme.css'
import classNames from 'classnames';
import { useTheme } from '../ThemeSwitch/ThemeContext';
const Bgc = () => {
const { isDarkTheme } = useTheme();
return (
    <div className={classNames('bgc_switcher', { 'dark-theme': isDarkTheme, 'light-theme': !isDarkTheme })}></div>
  );

    }
export default Bgc;
