import React from 'react';
import { useTheme } from './ThemeContext';
import classNames from 'classnames';
import styles from './theme_switcher.css';
import sunIcon from '../assets/sun.png'; // Путь к изображению солнца
import moonIcon from '../assets/moon.png'; // Путь к изображению луны

const ThemeSwitcher = () => {
  const { isDarkTheme, toggleTheme, themeStyles } = useTheme();

  return (
    <div className={classNames('theme-switcher-wrap', { 'dark-theme': isDarkTheme, 'light-theme': !isDarkTheme })}>
      <button
        onClick={toggleTheme}
        className={classNames('theme-switcher-button', { 'dark-theme': isDarkTheme, 'light-theme': !isDarkTheme })}
        style={{
          background: 'none', // Убираем фон кнопки
          border: 'none', // Убираем границу кнопки
          padding: 0, // Убираем внутренние отступы кнопки
          cursor: 'pointer',
        }}
      >
        <img
          src={isDarkTheme ? moonIcon : sunIcon}
          alt={isDarkTheme ? 'Moon icon' : 'Sun icon'}
          style={{
            width: '20px', // Размер изображения
            height: 'auto',
          }}
        />
      </button>
    </div>
  );
};

export default ThemeSwitcher;
