import React, { useState } from "react";
import solarEnergy from '../assets/Content pages/solarEnergy.png'
import styles from './content.css'

const ContentButton = ({ title, children }) => {
  const [isActive, setIsActive] = useState(false);

  return (
    <div className="content-button" onClick={() => setIsActive(!isActive)}>
      {title}
      <div className={`content1 ${isActive ? 'active' : ''}`}>
        {children}
      </div>
    </div>
  );
}

const Content = () => {
  return (
    <div className="content">
      <div className="content__container">
        <div className="text-content">
          <h1>Our commitment to green energy is paving the way for a cleaner, healthier planet. </h1>
          <ContentButton title="EV charging">
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </ContentButton>

          <ContentButton title="Solar Energy">
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </ContentButton>

          <ContentButton title="Wind Energy">
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </ContentButton>

          <ContentButton title="Hydropower">
            <p>Solar panels convert sunlight into electricity. Photovoltaic (PV) cells on these panels capture the energy from the sun and convert it into electrical power.</p>
            <a>Learn more</a>
          </ContentButton>
        </div>

        <div className="pic-content">
          <img src={solarEnergy} alt="Solar Energy" />
        </div>
      </div>
    </div>
  );
}

export default Content;
